import React from "react";
// TODO: import useFormik from formik library
import './index.css';
import {useFormik} from 'formik';
function App() {
  // TODO: add a const called formik assigned to useFormik()
const formik = useFormik({
    initialValues: {
      emailField: '',
      password: ''

    },
    onSubmit: values =>{
      console.log('form:', values);
    },
    validate: values => {
      let errors = {};
      if(!values.email) errors.emailError = 'Required';
      if(!values.password) errors.pswError = 'Required';
      return errors;
    }
});
  return (
    <div>
      <form onSubmit={formik.handleSubmit}>
        <div>Email</div>
        <input name="emailField" type="text" onChange={formik.handleChange} value={formik.values.emailField}/>
        {formik.errors.emailError ? <div style={{color:'red'}}>{formik.errors.emailError}</div>: null}

        <div>Password</div>
        <input name="pswField" type="text" onChange={formik.handleChange} value={formik.values.password}/>
        {formik.errors.pswError ? <div style={{color:'red'}}>{formik.errors.pswError}</div>: null}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
